package com.techment.day2_3;

public class pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k=1;
		for(int i =1;i<=3;i++)
		{
			for(int j=1;j<i+1;j++)
			{
				System.out.print(k++ + " ");  
			}
			System.out.print("\n");
		}
	}

}
